definition.script:
def REPO_APP_DIR = ''
def REPO_MAINT_DIR = ''
def skipRemainingStages = false
def APP_SOURCE_BRANCH = 'main'

pipeline {
    agent { node { label "docker-build-node" }}
    stages {
        stage('checkout-application-branch') {
            steps {
                updateGitlabCommitStatus name: 'checkout', state: 'running'
                echo "Checkout Application Repo"

                script {
                    // These are variables set by the GitLab Plugin
                    try {
                        echo "gitlabBranch            : ${gitlabBranch}"
                        echo "gitlabSourceBranch      : ${gitlabSourceBranch}"
                        echo "gitlabSourceRepoName    : ${gitlabSourceRepoName}"
                        echo "gitlabMergeRequestId    : ${gitlabMergeRequestId}"
                        //echo "gitlabMergeRequestState : ${gitlabMergeRequestState}"
                        echo "gitlabTargetBranch      : ${gitlabTargetBranch}"
                        echo "gitlabTriggerPhrase     : ${gitlabTriggerPhrase}"
                        APP_SOURCE_BRANCH = gitlabSourceBranch
                    } catch (Exception e) {
                        updateGitlabCommitStatus name: 'checkout', state: 'failed'
                        skipRemainingStages = true
                    }   
                }

                git branch: APP_SOURCE_BRANCH, credentialsId: 'jenkins-gitlab-ssh', url: 'git@gitlab:lab/application-repo-01.git'

                script {
                    def pwd = sh( 
                        script: 'pwd',
                        returnStdout: true
                    ).trim()
                    REPO_APP_DIR = pwd
                    echo "REPO_APP_DIR: ${REPO_APP_DIR}"
                    echo "Checking branch ${APP_SOURCE_BRANCH}"
                    def actual_branch = sh(
                        script: 'git status| head -1',
                        returnStdout: true
                    ).trim()
                    echo "actual_branch: ${actual_branch}"
                }
                echo "Checkout CI Repo"
            }
        }
        stage('checkout-maintenance-branch') {
            when {
                expression {
                    !skipRemainingStages
                }
            }
            steps {
                echo "Checkout Application Repo"

                git branch: 'main', credentialsId: 'jenkins-gitlab-ssh', url: 'git@gitlab:lab/deployment-maintenance.git'

                script {
                    def pwd = sh( 
                        script: 'pwd',
                        returnStdout: true
                    ).trim()
                    REPO_MAINT_DIR = pwd
                    echo "REPO_MAINT_DIR: ${REPO_MAINT_DIR}"
                }
                echo "Checkout CI Repo"
                updateGitlabCommitStatus name: 'checkout', state: 'success'
            }
        }
        stage('generate-helm-charts') {
            when {
                expression {
                    !skipRemainingStages
                }
            }
            steps {
                updateGitlabCommitStatus name: 'generate-helm-charts', state: 'running'
                echo "Generate Helm Charts"
                echo "REPO_APP_DIR: ${REPO_APP_DIR}"
                echo "REPO_MAINT_DIR: ${REPO_MAINT_DIR}"
                script {
                    try {
                        dir(REPO_MAINT_DIR){
                            def output = sh( 
                                script: "sh run.sh application_helm_integration.py ${env.BUILD_NUMBER} ${REPO_APP_DIR} ${APP_SOURCE_BRANCH} ${REPO_MAINT_DIR}",
                                returnStdout: true
                            ).trim()
                            echo "output: ${output}"
                        }
                        updateGitlabCommitStatus name: 'generate-helm-charts', state: 'success'
                    } catch (Exception e) {
                        updateGitlabCommitStatus name: 'generate-helm-charts', state: 'failed'
                        skipRemainingStages = true
                    }       
                }
            }
        }
        stage('commit-helm-charts') {
            when {
                expression {
                    !skipRemainingStages
                }
            }
            steps {
                updateGitlabCommitStatus name: 'commit-helm-charts', state: 'running'
                echo "Commit Helm Charts"
                /*
                sshagent (credentials: ['git-ssh-credentials-ID']) {
                    sh("git tag -a some_tag -m 'Jenkins'")
                    sh('git push <REPO> --tags')
                }
                */
                updateGitlabCommitStatus name: 'commit-helm-charts', state: 'success'
            }
        }
        stage('cleanup') {
            when {
                expression {
                    !skipRemainingStages
                }
            }
            steps {
                updateGitlabCommitStatus name: 'cleanup', state: 'running'
                echo "DONE"
                updateGitlabCommitStatus name: 'cleanup', state: 'success'
            }
        }
    }
}
